const rates=document.querySelectorAll(".slider__item-rate");function rate(){rates.forEach(t=>{if(t.hasAttribute("data-rate")){var r=t.getAttribute("data-rate").toString(),n=5-r;for(let e=0;e<r;e++){const s=document.createElement("img");s.setAttribute("src","images/star-fill.svg"),t.appendChild(s)}for(let e=0;e<n;e++){const l=document.createElement("img");l.setAttribute("src","images/star.svg"),t.appendChild(l)}}else{const e=document.createElement("img");e.setAttribute("src","images/star-fill.svg"),t.appendChild(e);for(let e=0;e<4;e++){const o=document.createElement("img");o.setAttribute("src","images/star.svg"),t.appendChild(o)}}})}function slider(e){const t=document.querySelector("."+e);e=document.querySelectorAll(".slider__item");const r=parseInt(window.getComputedStyle(e[0]).getPropertyValue("flex-basis").substring(0,3)),n=parseInt(window.getComputedStyle(e[0]).getPropertyValue("margin-right").substring(0,2));var s=parseInt(window.getComputedStyle(t).getPropertyValue("height").substring(0,3));parseInt(window.getComputedStyle(t).getPropertyValue("padding-bottom").substring(0,2));const l=t.closest(".slider__content-inner"),o=document.querySelector(".slider__btn-right");l.style.width=e.length*r+(e.length-1)*n+"px",l.style.height=s+"px",console.log(o);let i=window.getComputedStyle(t).getPropertyValue("left");i=parseInt(i.substring(0,i.length-2)),console.log(i),o.addEventListener("click",()=>{i=i-r-n,t.style.left=i+"px"})}rate(),document.addEventListener("DOMContentLoaded",()=>{slider("slider__content")});