const rates=document.querySelectorAll(".slider__item-rate");function rate(){rates.forEach(e=>{if(e.hasAttribute("data-rate")){var r=e.getAttribute("data-rate").toString(),s=5-r;for(let t=0;t<r;t++){const a=document.createElement("img");a.setAttribute("src","images/star-fill.svg"),e.appendChild(a)}for(let t=0;t<s;t++){const i=document.createElement("img");i.setAttribute("src","images/star.svg"),e.appendChild(i)}}else{const t=document.createElement("img");t.setAttribute("src","images/star-fill.svg"),e.appendChild(t);for(let t=0;t<4;t++){const n=document.createElement("img");n.setAttribute("src","images/star.svg"),e.appendChild(n)}}})}function slider(t){document.querySelector("."+t)}slider("slider__content"),rate();