const rates=document.querySelectorAll(".slider__item-rate");function rate(){rates.forEach(t=>{if(t.hasAttribute("data-rate")){var r=t.getAttribute("data-rate").toString(),s=5-r;for(let e=0;e<r;e++){const a=document.createElement("img");a.setAttribute("src","images/star-fill.svg"),t.appendChild(a)}for(let e=0;e<s;e++){const i=document.createElement("img");i.setAttribute("src","images/star.svg"),t.appendChild(i)}}else{const e=document.createElement("img");e.setAttribute("src","images/star-fill.svg"),t.appendChild(e);for(let e=0;e<4;e++){const l=document.createElement("img");l.setAttribute("src","images/star.svg"),t.appendChild(l)}}})}function slider(e){document.querySelector("."+e);e=document.querySelectorAll(".slider__item");parseInt(window.getComputedStyle(e[0]).getPropertyValue("flex-basis").substr(0,3))}slider("slider__content"),rate();